import React, { Fragment, useEffect, useContext, useState, useRef } from "react";
import OrderSuccessMessage from "./OrderSuccessMessage";
import { HomeContext } from "./";
import { sliderImages } from "../../admin/dashboardAdmin/Action";
import { prevSlide, nextSlide } from "./Mixins";

const Slider = () => {
  const { data, dispatch } = useContext(HomeContext);
  const [slide, setSlide] = useState(0);
  const intervalRef = useRef(null);

  useEffect(() => {
    sliderImages(dispatch);
  }, [dispatch]);

  // Auto Slide every 4 seconds
  useEffect(() => {
    if (data?.sliderImages?.length > 1) {
      intervalRef.current = setInterval(() => {
        setSlide((prev) =>
          prev === data.sliderImages.length - 1 ? 0 : prev + 1
        );
      }, 4000);
    }
    return () => clearInterval(intervalRef.current);
  }, [data?.sliderImages]);

  return (
    <Fragment>
      <div className="relative mt-12 h-[280px] overflow-hidden rounded-xl shadow-md border border-gray-300">
        {data?.sliderImages?.length > 0 && (
          <img
            key={slide}
            src={`http://localhost:8000/uploads/customize/${data.sliderImages[slide].slideImage}`}
            alt="slider"
            className="w-full h-full object-cover transition-opacity duration-700 ease-in-out"
          />
        )}

        {/* Left Arrow */}
        <svg
          onClick={() =>
            prevSlide(data.sliderImages.length, slide, setSlide)
          }
          className="z-10 absolute top-1/2 -translate-y-1/2 left-3 w-9 h-9 text-white bg-black/40 hover:bg-black/70 rounded-full p-2 cursor-pointer"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
        </svg>

        {/* Right Arrow */}
        <svg
          onClick={() =>
            nextSlide(data.sliderImages.length, slide, setSlide)
          }
          className="z-10 absolute top-1/2 -translate-y-1/2 right-3 w-9 h-9 text-white bg-black/40 hover:bg-black/70 rounded-full p-2 cursor-pointer"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
        </svg>

        {/* Shop Now Button */}
        <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2">
          <a
            href="#shop"
            className="bg-yellow-500 hover:bg-yellow-600 transition-colors px-5 py-2 text-white text-base rounded-full shadow"
          >
            Shop Now
          </a>
        </div>
      </div>

      <OrderSuccessMessage />
    </Fragment>
  );
};

export default Slider;
