import React, { Fragment, useEffect, useContext, useState } from "react";
import OrderSuccessMessage from "./OrderSuccessMessage";
import { HomeContext } from "./";
import { sliderImages } from "../../admin/dashboardAdmin/Action";
import { prevSlide, nextSlide } from "./Mixins";

const Slider = () => {
  const { data, dispatch } = useContext(HomeContext);
  const [slide, setSlide] = useState(0);

  useEffect(() => {
    sliderImages(dispatch);
  }, [dispatch]);

  return (
    <Fragment>
      <div className="relative mt-16 h-[400px] overflow-hidden rounded-2xl shadow-lg border-2 border-gray-200">
        {data?.sliderImages?.length > 0 && (
          <img
            key={slide}
            src={`http://localhost:8000/uploads/customize/${data.sliderImages[slide].slideImage}`}
            alt="sliderImage"
            className="w-full h-full object-cover transition-opacity duration-700 ease-in-out opacity-100"
          />
        )}

        {/* Left Arrow */}
        <svg
          onClick={() =>
            prevSlide(data.sliderImages.length, slide, setSlide)
          }
          className="z-10 absolute top-1/2 -translate-y-1/2 left-4 w-10 h-10 text-white bg-black/40 hover:bg-black/70 rounded-full p-2 cursor-pointer"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
        </svg>

        {/* Right Arrow */}
        <svg
          onClick={() =>
            nextSlide(data.sliderImages.length, slide, setSlide)
          }
          className="z-10 absolute top-1/2 -translate-y-1/2 right-4 w-10 h-10 text-white bg-black/40 hover:bg-black/70 rounded-full p-2 cursor-pointer"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
        </svg>

        {/* Shop Now Button */}
        <div className="absolute bottom-10 left-1/2 transform -translate-x-1/2">
          <a
            href="#shop"
            className="bg-yellow-500 hover:bg-yellow-600 transition-colors px-6 py-3 text-white text-lg rounded-full shadow-md"
          >
            Shop Now
          </a>
        </div>
      </div>

      <OrderSuccessMessage />
    </Fragment>
  );
};

export default Slider;
