import React, { Fragment, useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { getAllProduct } from "../../admin/products/FetchApi";
import { HomeContext } from "./index";
import { isWishReq, unWishReq, isWish } from "./Mixins";

const SingleProduct = () => {
  const { data, dispatch } = useContext(HomeContext);
  const { products } = data;
  const navigate = useNavigate();
  const [wList, setWlist] = useState(JSON.parse(localStorage.getItem("wishList")));

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    dispatch({ type: "loading", payload: true });
    try {
      const responseData = await getAllProduct();
      setTimeout(() => {
        if (responseData?.Products) {
          dispatch({ type: "setProducts", payload: responseData.Products });
          dispatch({ type: "loading", payload: false });
        }
      }, 500);
    } catch (error) {
      console.error(error);
    }
  };

  if (data.loading) {
    return (
      <div className="flex justify-center items-center min-h-[300px]">
        <div className="animate-spin w-10 h-10 border-4 border-yellow-600 border-t-transparent rounded-full"></div>
      </div>
    );
  }

  return (
    <Fragment>
      <div className="px-4 sm:px-6 lg:px-8 max-w-screen-xl mx-auto">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-10">All Products</h2>

        {products && products.length > 0 ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
            {products.map((item, index) => (
              <div key={index} className="bg-white shadow-md rounded-lg overflow-hidden hover:shadow-xl transition-shadow duration-300 relative">
                <img
                  onClick={() => navigate(`/products/${item._id}`)}
                  src={`http://localhost:8000/uploads/products/${item.pImages[0]}`}
                  alt={item.pName}
                  className="w-full h-52 object-cover cursor-pointer"
                />

                <div className="p-4">
                  <h3 className="text-lg font-semibold text-gray-800 truncate">{item.pName}</h3>
                  <p className="text-yellow-700 mt-1">${item.pPrice}.00</p>
                  <div className="flex items-center mt-2">
                    <svg
                      className="w-4 h-4 fill-current text-yellow-700 mr-1"
                      viewBox="0 0 24 24"
                    >
                      <path
                        fill="currentColor"
                        d="M12 .587l3.668 7.571 8.332 1.151-6.064 5.875 1.484 8.282L12 18.896l-7.42 4.57 1.484-8.282L.002 9.309l8.332-1.151z"
                      />
                    </svg>
                    <span className="text-sm text-gray-600">{item.pRatingsReviews.length} reviews</span>
                  </div>
                </div>

                {/* Wishlist Icons */}
                <div className="absolute top-2 right-2">
                  {!isWish(item._id, wList) ? (
                    <svg
                      onClick={(e) => isWishReq(e, item._id, setWlist)}
                      className="w-6 h-6 text-yellow-600 cursor-pointer"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
                      />
                    </svg>
                  ) : (
                    <svg
                      onClick={(e) => unWishReq(e, item._id, setWlist)}
                      className="w-6 h-6 text-yellow-600 cursor-pointer"
                      fill="currentColor"
                      viewBox="0 0 20 20"
                    >
                      <path
                        fillRule="evenodd"
                        d="M3.172 5.172a4 4 0 015.656 0L10 6.343l1.172-1.171a4 4 0 115.656 5.656L10 17.657l-6.828-6.829a4 4 0 010-5.656z"
                        clipRule="evenodd"
                      />
                    </svg>
                  )}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center text-xl text-gray-600 mt-16">No product found</div>
        )}
      </div>
    </Fragment>
  );
};

export default SingleProduct;
